// frontend/src/pages/admin/ProductForm.jsx
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addProduct, editProduct } from '../../store/slices/productSlice';

const ProductForm = ({ product = null, isEdit = false }) => {
  const [formData, setFormData] = useState(
    product || { name: '', price: 0, category: '', description: '', countInStock: 0 }
  );
  const dispatch = useDispatch();

  const submitHandler = (e) => {
    e.preventDefault();
    if (isEdit) {
      dispatch(editProduct({ id: product._id, product: formData }));
    } else {
      dispatch(addProduct(formData));
    }
  };

  return (
    <form onSubmit={submitHandler}>
      <input
        type="text"
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        placeholder="Назва продукту"
      />
      <input
        type="number"
        value={formData.price}
        onChange={(e) => setFormData({ ...formData, price: e.target.value })}
        placeholder="Ціна"
      />
      <input
        type="text"
        value={formData.category}
        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
        placeholder="Категорія"
      />
      <textarea
        value={formData.description}
        onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        placeholder="Опис"
      />
      <input
        type="number"
        value={formData.countInStock}
        onChange={(e) => setFormData({ ...formData, countInStock: e.target.value })}
        placeholder="Кількість на складі"
      />
      <button type="submit">{isEdit ? 'Оновити' : 'Додати'}</button>
    </form>
  );
};

export default ProductForm;
