// src/components/layout/Header/Header.jsx
import { Search, ShoppingCart, Menu, X, LogOut, User, Settings, Package } from 'lucide-react';
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../../../store/slices/authSlice';

export function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showWelcome, setShowWelcome] = useState(false);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  // –û—Ç—Ä–∏–º—É—î–º–æ –¥–∞–Ω—ñ –∫–æ—Ä–∏—Å—Ç—É–≤–∞—á–∞ –∑ Redux store
  const { user } = useSelector((state) => state.auth);
  const cartItems = useSelector((state) => state.cart.items);

  // –ü–æ–∫–∞–∑—É—î–º–æ –ø—Ä–∏–≤—ñ—Ç–∞–ª—å–Ω–µ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è –ø—Ä–∏ –ª–æ–≥—ñ–Ω—ñ
  useEffect(() => {
    if (user) {
      setShowWelcome(true);
      const timer = setTimeout(() => {
        setShowWelcome(false);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [user]);

  const handleLogout = () => {
    dispatch(logout());
    navigate('/');
  };

  return (
    <>
      {/* –ü—Ä–∏–≤—ñ—Ç–∞–ª—å–Ω–µ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è */}
      {showWelcome && user && (
        <div className="fixed top-4 right-4 bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded z-50 shadow-lg">
          <p className="font-medium">Welcome back, {user.firstName}!</p>
        </div>
      )}

      <header className="bg-gray-900 text-white sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <div className="flex-shrink-0">
              <Link to="/" className="flex items-center">
                <span className="text-2xl font-bold text-blue-500">üõ†</span>
                <span className="ml-2 text-xl font-bold">HardwareHub</span>
              </Link>
            </div>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center space-x-8">
              <Link to="/" className="hover:text-gray-300 transition duration-150">
                Home
              </Link>
              <Link to="/products" className="hover:text-gray-300 transition duration-150">
                Products
              </Link>
              <Link to="/contacts" className="hover:text-gray-300 transition duration-150">
                Contacts
              </Link>
            </nav>

            {/* Search Bar */}
            <div className="hidden md:flex flex-1 max-w-md mx-8">
              <div className="relative w-full">
                <input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full bg-gray-800 rounded-lg py-2 pl-4 pr-10 focus:outline-none focus:ring-2 focus:ring-blue-500 text-gray-300 placeholder-gray-400"
                />
                <Search className="absolute right-3 top-2.5 h-5 w-5 text-gray-400" />
              </div>
            </div>

            {/* User Section */}
            <div className="hidden md:flex items-center space-x-4">
              <Link to="/cart" className="relative hover:text-gray-300">
                <ShoppingCart className="h-6 w-6" />
                {cartItems?.length > 0 && (
                  <span className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                    {cartItems.length}
                  </span>
                )}
              </Link>

              {user ? (
                <div className="relative group">
                  <button className="flex items-center space-x-2 hover:text-gray-300">
                    <User className="h-6 w-6" />
                    <span>
                      {user.firstName} {user.lastName}
                    </span>
                  </button>
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 hidden group-hover:block">
                    {user.isAdmin && (
                      <Link
                        to="/admin"
                        className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                      >
                        <Settings className="h-4 w-4 mr-2" />
                        Admin Panel
                      </Link>
                    )}
                    <Link
                      to="/orders"
                      className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                    >
                      <Package className="h-4 w-4 mr-2" />
                      My Orders
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                    >
                      <LogOut className="h-4 w-4 mr-2" />
                      Logout
                    </button>
                  </div>
                </div>
              ) : (
                <div className="space-x-4">
                  <Link to="/login" className="hover:text-gray-300 transition duration-150">
                    Login
                  </Link>
                  <Link
                    to="/register"
                    className="bg-blue-600 px-4 py-2 rounded-lg hover:bg-blue-700 transition duration-150"
                  >
                    Register
                  </Link>
                </div>
              )}
            </div>

            {/* Mobile Menu Button */}
            <div className="md:hidden">
              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="inline-flex items-center justify-center p-2 rounded-md hover:bg-gray-800"
              >
                {isMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
              </button>
            </div>
          </div>

          {/* Mobile Menu */}
          {isMenuOpen && (
            <div className="md:hidden py-4">
              <div className="px-2 pt-2 pb-3 space-y-1">
                {/* Mobile Search */}
                <div className="relative mb-4">
                  <input
                    type="text"
                    placeholder="Search products..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full bg-gray-800 rounded-lg py-2 pl-4 pr-10 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  <Search className="absolute right-3 top-2.5 h-5 w-5 text-gray-400" />
                </div>

                {/* Mobile Navigation */}
                <Link to="/" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                  Home
                </Link>
                <Link to="/products" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                  Products
                </Link>
                <Link to="/contacts" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                  Contacts
                </Link>
                <Link to="/cart" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                  Cart ({cartItems?.length || 0})
                </Link>

                {/* Mobile User Section */}
                {user ? (
                  <>
                    <div className="px-3 py-2 text-gray-300">
                      Signed in as {user.firstName} {user.lastName}
                    </div>
                    {user.isAdmin && (
                      <Link to="/admin" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                        Admin Panel
                      </Link>
                    )}
                    <Link to="/orders" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                      My Orders
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-3 py-2 rounded-md hover:bg-gray-800"
                    >
                      Logout
                    </button>
                  </>
                ) : (
                  <div className="space-y-2">
                    <Link to="/login" className="block px-3 py-2 rounded-md hover:bg-gray-800">
                      Login
                    </Link>
                    <Link
                      to="/register"
                      className="block px-3 py-2 bg-blue-600 rounded-md hover:bg-blue-700 text-center"
                    >
                      Register
                    </Link>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </header>
    </>
  );
}

export default Header;
