import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  // Search,
  // ShoppingCart,
  // Menu,
  // X,
  // LogOut,
  Paintbrush,
  Ruler,
  Wrench,
  Tool,
  ChevronRight,
} from 'lucide-react';

const categories = [
  {
    id: 1,
    name: 'Power Tools',
    description: 'Professional-grade power tools for every project',
    icon: Tool,
    color: 'blue',
    path: '/products?category=power-tools',
  },
  {
    id: 2,
    name: 'Paint & Supplies',
    description: 'Premium paints and supplies for any surface',
    icon: Paintbrush,
    color: 'green',
    path: '/products?category=paint-supplies',
  },
  {
    id: 3,
    name: 'Measuring Tools',
    description: 'Precise measuring tools for accurate results',
    icon: Ruler,
    color: 'red',
    path: '/products?category=measuring-tools',
  },
  {
    id: 4,
    name: 'Plumbing',
    description: 'Complete range of plumbing tools and supplies',
    icon: Wrench,
    color: 'purple',
    path: '/products?category=plumbing',
  },
];

export default function Hero() {
  const navigate = useNavigate();
  const [activeCategory, setActiveCategory] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);

  // Обробка скролу для анімації
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Навігація до магазину
  const handleShopNow = () => {
    navigate('/products');
  };

  // Навігація до каталогу з фільтром категорії
  const handleCategoryClick = (path) => {
    navigate(path);
  };

  return (
    <div className="w-full bg-white">
      {/* Main Banner */}
      <div
        className={`relative w-full transition-all duration-500 ${
          isScrolled ? 'h-[400px]' : 'h-[600px]'
        }`}
      >
        <div
          className="absolute inset-0 bg-cover bg-center transition-transform duration-300 transform hover:scale-105"
          style={{
            backgroundImage:
              "url('https://images.unsplash.com/photo-1581783898377-1c85bf937427?auto=format&fit=crop&q=80')",
          }}
        >
          <div className="absolute inset-0 bg-black bg-opacity-50" />
        </div>
        <div className="relative h-full max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex flex-col justify-center">
          <h1
            className="text-4xl md:text-6xl font-bold text-white mb-4 animate-fade-in"
            style={{ animationDelay: '0.2s' }}
          >
            Build Your Dreams With Us
          </h1>
          <p
            className="text-xl md:text-2xl text-gray-200 mb-8 max-w-2xl animate-fade-in"
            style={{ animationDelay: '0.4s' }}
          >
            Discover premium tools and equipment for all your DIY projects and professional needs
          </p>
          <div
            className="flex flex-col sm:flex-row gap-4 animate-fade-in"
            style={{ animationDelay: '0.6s' }}
          >
            <button
              onClick={handleShopNow}
              className="px-8 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-300 text-lg font-semibold transform hover:scale-105 flex items-center justify-center"
            >
              Shop Now
              <ChevronRight className="ml-2 h-5 w-5" />
            </button>
            <button
              onClick={() => navigate('/products')}
              className="px-8 py-3 bg-white text-gray-900 rounded-lg hover:bg-gray-100 transition duration-300 text-lg font-semibold transform hover:scale-105"
            >
              View Catalog
            </button>
          </div>
        </div>
      </div>

      {/* Categories Grid */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <h2 className="text-3xl font-bold text-gray-900 mb-12 text-center animate-fade-in">
          Popular Categories
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {categories.map((category) => {
            const Icon = category.icon;
            return (
              <div
                key={category.id}
                onClick={() => handleCategoryClick(category.path)}
                onMouseEnter={() => setActiveCategory(category.id)}
                onMouseLeave={() => setActiveCategory(null)}
                className={`bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition duration-300 cursor-pointer transform hover:-translate-y-1 ${
                  activeCategory === category.id ? `ring-2 ring-${category.color}-400` : ''
                }`}
              >
                <div
                  className={`bg-${category.color}-100 w-12 h-12 rounded-lg flex items-center justify-center mb-4`}
                >
                  <Icon className={`h-6 w-6 text-${category.color}-600`} />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">{category.name}</h3>
                <p className="text-gray-600">{category.description}</p>
              </div>
            );
          })}
        </div>
      </div>

      {/* Scroll to Top Button */}
      {isScrolled && (
        <button
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          className="fixed bottom-8 right-8 bg-blue-600 text-white p-3 rounded-full shadow-lg hover:bg-blue-700 transition duration-300 transform hover:scale-110"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M5 10l7-7m0 0l7 7m-7-7v18"
            />
          </svg>
        </button>
      )}
    </div>
  );
}

// Додаємо стилі для анімацій
const styles = `
  @keyframes fade-in {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  .animate-fade-in {
    animation: fade-in 0.6s ease-out forwards;
    opacity: 0;
  }
`;

// Додаємо стилі до head
const styleSheet = document.createElement('style');
styleSheet.innerText = styles;
document.head.appendChild(styleSheet);

