// src/pages/orders/OrderSuccessPage.jsx
import React, { useEffect } from 'react';
import { useLocation, Link, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { CheckCircle } from 'lucide-react';
import { getOrderDetails } from '../../store/slices/ordersSlice';

export function OrderSuccessPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const orderId = location.state?.orderId;
  const { currentOrder, loading, error } = useSelector((state) => state.orders);

  useEffect(() => {
    if (orderId) {
      dispatch(getOrderDetails(orderId));
    } else {
      navigate('/orders');
    }
  }, [orderId, dispatch, navigate]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 py-12">
        <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white rounded-lg shadow-md p-8 text-center">
            <div className="animate-pulse">
              <div className="h-16 w-16 mx-auto bg-gray-200 rounded-full mb-4"></div>
              <div className="h-8 bg-gray-200 rounded w-3/4 mx-auto mb-4"></div>
              <div className="h-4 bg-gray-200 rounded w-1/2 mx-auto"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 py-12">
        <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white rounded-lg shadow-md p-8 text-center">
            <div className="text-red-600 mb-4">Error loading order details</div>
            <Link
              to="/orders"
              className="inline-block bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
            >
              View All Orders
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (!currentOrder) return null;

  return (
    <div className="min-h-screen bg-gray-100 py-12">
      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-md p-8">
          <div className="text-center mb-8">
            <CheckCircle className="mx-auto h-16 w-16 text-green-500" />
            <h1 className="mt-4 text-3xl font-bold text-gray-900">Order Placed Successfully!</h1>
            <p className="mt-2 text-lg text-gray-600">
              Thank you for your order. We'll send you a confirmation email shortly.
            </p>
          </div>

          <div className="border-t border-gray-200 pt-8">
            <h2 className="text-xl font-semibold mb-4">Order Details</h2>
            <div className="space-y-4">
              <p>
                <span className="font-medium">Order ID:</span> {currentOrder._id.slice(-8)}
              </p>
              <p>
                <span className="font-medium">Status:</span>{' '}
                <span className="capitalize">{currentOrder.status}</span>
              </p>
              <p>
                <span className="font-medium">Date:</span>{' '}
                {new Date(currentOrder.createdAt).toLocaleDateString()}
              </p>
            </div>

            <div className="mt-6">
              <h3 className="text-lg font-semibold mb-3">Items</h3>
              <div className="space-y-4">
                {currentOrder.items.map((item) => (
                  <div key={item._id} className="flex justify-between items-center border-b pb-4">
                    <div>
                      <p className="font-medium">{item.product.name}</p>
                      <p className="text-sm text-gray-500">Quantity: {item.quantity}</p>
                    </div>
                    <p className="font-medium">${item.price.toFixed(2)}</p>
                  </div>
                ))}
              </div>
            </div>

            <div className="mt-6 space-y-2">
              <div className="flex justify-between">
                <span>Subtotal</span>
                <span>
                  $
                  {currentOrder.items
                    .reduce((sum, item) => sum + item.price * item.quantity, 0)
                    .toFixed(2)}
                </span>
              </div>
              <div className="flex justify-between">
                <span>Shipping</span>
                <span>$9.99</span>
              </div>
              <div className="flex justify-between">
                <span>Tax</span>
                <span>
                  $
                  {(
                    currentOrder.items.reduce((sum, item) => sum + item.price * item.quantity, 0) *
                    0.1
                  ).toFixed(2)}
                </span>
              </div>
              <div className="flex justify-between font-semibold text-lg border-t pt-4">
                <span>Total</span>
                <span>${currentOrder.totalAmount.toFixed(2)}</span>
              </div>
            </div>

            <div className="mt-8 space-x-4">
              <Link
                to="/orders"
                className="inline-block bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
              >
                View All Orders
              </Link>
              <Link
                to="/products"
                className="inline-block bg-gray-200 text-gray-700 px-6 py-2 rounded-lg hover:bg-gray-300"
              >
                Continue Shopping
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderSuccessPage;
